svg.script:
const svg = document.currentScript.ownerDocument;
const g = svg.getElementById('stars');
function rnd(a,b){ return a + Math.random()*(b-a); }
const N = 220;
for(let i=0;i<N;i++){
  const c = svg.createElementNS('http://www.w3.org/2000/svg','circle');
  c.setAttribute('cx', rnd(0,1920));
  c.setAttribute('cy', rnd(0,1080));
  const r = rnd(0.6, 2.2);
  c.setAttribute('r', r);
  c.setAttribute('fill', '#ffffff');
  c.setAttribute('fill-opacity', rnd(0.12,0.55));
  const a = svg.createElementNS('http://www.w3.org/2000/svg','animate');
  a.setAttribute('attributeName','fill-opacity');
  const o1 = rnd(0.10,0.25), o2 = rnd(0.45,0.75);
  a.setAttribute('values', `${o1};${o2};${o1}`);
  a.setAttribute('dur', `${rnd(1.2,3.6)}s`);
  a.setAttribute('repeatCount','indefinite');
  c.appendChild(a);
  g.appendChild(c);
}
